Widget c = null;
try {
    Capsule cap = new Capsule(GWT.getHostPageBaseURL() + "media/o-na-som.mp3",
            false);
    cap.showLogger(true);
    c = cap;
} catch (LoadException ex) {
    Window.alert("Load exp");
} catch (PluginVersionException ex) {
    c = PlayerUtil.getMissingPluginNotice(Plugin.Auto, "No Plugin",
            "A compatible sound plugin could not be found", false);
} catch (PluginNotFoundException ex) {
    c = PlayerUtil.getMissingPluginNotice(Plugin.Auto, "No Plugin",
            "A compatible sound plugin could not be found", false);
}
panel.add(c);
