Widget v = null;
try {
    v = new FlatVideoPlayer(Plugin.WinMediaPlayer, "http://bst-player.googlecode.com/" +
        "svn/tags/showcase/media/teaching-of-islam.wmv", false, "300px", "100%");
} catch (LoadException ex) {
    Window.alert("Load exp");
} catch (PluginVersionException ex) {
    v = PlayerUtil.getMissingPluginNotice(Plugin.WinMediaPlayer, ex.getRequiredVersion());
} catch (PluginNotFoundException ex) {
    v = PlayerUtil.getMissingPluginNotice(Plugin.WinMediaPlayer);
}
panel.add(v);

