Widget v = null;
try {
    FlatVideoPlayer vp = new FlatVideoPlayer(Plugin.FlashVideoPlayer,
        "http://vixy.net/flv/vixy_net_podcaster_demo_director.flv",
        false, "350px", "100%");
    vp.showLogger(true);
    v = vp;
} catch (LoadException ex) {
    Window.alert("Load exp");
} catch (PluginVersionException ex) {
    v = PlayerUtil.getMissingPluginNotice(Plugin.FlashVideoPlayer,
        ex.getRequiredVersion());
} catch (PluginNotFoundException ex) {
    v = PlayerUtil.getMissingPluginNotice(Plugin.FlashVideoPlayer);
}
panel.add(v);

Widget v = null;
try {
    FlatVideoPlayer p = new FlatVideoPlayer(GWT.getHostPageBaseURL() +
            "media/sample.mov", false, "300px", "100%");
    p.showLogger(true);
    v = p;
} catch (LoadException ex) {
    Window.alert("Load exp");
} catch (PluginVersionException ex) {
    v = PlayerUtil.getMissingPluginNotice(Plugin.Auto, "Missing Plugin",
            "No compatible video player plugin could be found", false);
} catch (PluginNotFoundException ex) {
    v = PlayerUtil.getMissingPluginNotice(Plugin.Auto, "Missing Plugin",
            "No compatible video player plugin could be found", false);
}
panel.add(v);
