    qt = PlayerUtil.getMissingPluginNotice(Plugin.QuickTimePlayer);
}
panel.add(qt);

Widget qt = null;
try {
    QuickTimePlayer p = new QuickTimePlayer(GWT.getHostPageBaseURL() +
        "media/o-na-som.mp3", false);
    p.showLogger(true);
    qt = p;
} catch (LoadException ex) {
    Window.alert("Load exp");
} catch (PluginVersionException ex) {
    qt = PlayerUtil.getMissingPluginNotice(Plugin.QuickTimePlayer);
} catch (PluginNotFoundException ex) {
    qt = PlayerUtil.getMissingPluginNotice(Plugin.QuickTimePlayer);
}
panel.add(qt);

Widget qt = null;
try {
    QuickTimePlayer p = new QuickTimePlayer("http://movies.apple.com/movies/" +
       "paramount/star_trek/startrek-tlr3_h.640.mov", false, "220px", "100%");
    p.showLogger(true);
    qt = p;
} catch (LoadException ex) {
    Window.alert("Load exp");
} catch (PluginVersionException ex) {
    qt = PlayerUtil.getMissingPluginNotice(Plugin.QuickTimePlayer,
            ex.getRequiredVersion());
} catch (PluginNotFoundException ex) {
    qt = PlayerUtil.getMissingPluginNotice(Plugin.QuickTimePlayer);
}
panel.add(qt);
